#!/bin/bash\n# Smartclip AI Launcher Script for Linux/macOS\n\necho \"=== Smartclip AI Launcher ===\"\necho \"Starting Smartclip AI Application...\"\necho \"\"\n\n# Check if Python is available\nif ! command -v python3 &> /dev/null; then\n    echo \"âŒ Python 3 is not installed or not in PATH\"\n    echo \"Please install Python 3.8 or higher\"\n    exit 1\nfi\n\n# Get the script directory\nSCRIPT_DIR=\"$(cd \"$(dirname \"${BASH_SOURCE[0]}\")\" && pwd)\"\ncd \"$SCRIPT_DIR\"\n\n# Check if virtual environment exists\nif [ ! -d \"smartclip_env\" ]; then\n    echo \"âŒ Virtual environment not found!\"\n    echo \"Please run setup.py first to install the application.\"\n    echo \"\"\n    echo \"Run: python3 setup.py\"\n    exit 1\nfi\n\n# Activate virtual environment\necho \"ðŸ”„ Activating virtual environment...\"\nsource smartclip_env/bin/activate\n\n# Check if main.py exists\nif [ ! -f \"main.py\" ]; then\n    echo \"âŒ main.py not found in current directory\"\n    echo \"Please ensure you're in the correct directory\"\n    exit 1\nfi\n\n# Set environment variables for better performance\nexport PYTORCH_CUDA_ALLOC_CONF=max_split_size_mb:512\nexport OMP_NUM_THREADS=4\nexport MKL_NUM_THREADS=4\n\n# Check if GPU is available\necho \"ðŸ” Checking system capabilities...\"\npython3 -c \"import torch; print(f'GPU Available: {torch.cuda.is_available()}')\" 2>/dev/null || echo \"PyTorch not installed\"\n\necho \"ðŸš€ Launching Smartclip AI...\"\necho \"\"\n\n# Launch the application\npython3 main.py\n\n# Check exit code\nEXIT_CODE=$?\nif [ $EXIT_CODE -ne 0 ]; then\n    echo \"\"\n    echo \"âŒ Application exited with error code: $EXIT_CODE\"\n    echo \"Check the logs above for error details\"\n    echo \"\"\n    echo \"Troubleshooting:\"\n    echo \"1. Ensure all dependencies are installed\"\n    echo \"2. Check if input files exist and are accessible\"\n    echo \"3. Verify system has enough disk space\"\n    echo \"4. Check if required AI models are downloaded\"\n    echo \"\"\n    echo \"For detailed troubleshooting, see README.md\"\nelse\n    echo \"âœ… Application completed successfully\"\nfi\n\necho \"\"\necho \"Press any key to exit...\"\nread -n 1 -s\n\n# Deactivate virtual environment\ndeactivate 2>/dev/null || true